using AutoMapper;
using Microsoft.AspNetCore.Mvc;
using Moq;
using System.Collections.Generic;
using System.Threading.Tasks;
using TrabajoInterno_Api_Imagen.Controllers;
using TrabajoInterno_Api_Imagen.DTOs;
using TrabajoInterno_Api_Imagen.Interfaces;
using Xunit;

namespace TrabajoInterno_Test_Imagen
{
    public class ImagenControllerTest
    {
        #region Property
        private readonly ImagenController imagenController;
        private readonly Mock<IImagenService> mockService;
        private readonly ImagenDto imagen = new()
        {
            Nombre = "Nombre",
            Id_Persona = 1,
            ImagenData = ""
        };
         private readonly ImagenDto imagenRes = new()
        {
             Id = "1",
             Nombre = "Nombre",
            Id_Persona = 1,
            ImagenData = ""
        };
        #endregion

        public ImagenControllerTest()
        {
            mockService = new Mock<IImagenService>();

            imagenController = new ImagenController(mockService.Object);
        }

        [Fact]
        public async Task GetAllImagen()
        {
             var list = new List<ImagenDto> { imagenRes };
            mockService.Setup(s => s.GetAll().Result).Returns(list);

            var resCall = await imagenController.GetAll();
            var res = resCall.Result as OkObjectResult;
            var listres = res?.Value;

            mockService.Verify(s => s.GetAll());
            Assert.Equal(200, res?.StatusCode);
            Assert.NotNull(listres);
        }
        [Fact]
        public async Task GetByIdImagen()
        {
            mockService.Setup(s => s.GetById("1").Result).Returns(imagenRes);

            var resCall = await imagenController.GetById("1");
            var res = resCall.Result as OkObjectResult;

            mockService.Verify(s => s.GetById("1"));
            Assert.Equal(200, res?.StatusCode);

        }
        [Fact]
        public async Task PostImagen()
        {
            mockService.Setup(s => s.Insert(this.imagen).Result).Returns(imagenRes);

            ImagenDto imagenDto = new()
            {
                Nombre = "Nombre",
                Id_Persona = 1,
                ImagenData = ""
            };

            var resCall = await imagenController.Post(imagenDto);
            var res = resCall.Result as OkObjectResult;

            Assert.Equal(200, res?.StatusCode);
        }

        [Fact]
        public async Task PutImagen()
        {
            mockService.Setup(s => s.Insert(imagen).Result).Returns(imagenRes);
            mockService.Setup(s => s.Update(imagen).Result).Returns(imagenRes);

            ImagenDto imagenDto = new()
            {
                Id = "1",
                Nombre = "Nombre",
                Id_Persona = 1,
                ImagenData = ""
            };


            var resCall = await imagenController.Put(imagenDto, "1");
            var res = resCall.Result as OkObjectResult;

            Assert.Equal(200, res?.StatusCode);

        }

        [Fact]
        public async Task DeleteImagen()
        {
            mockService.Setup(s => s.GetById(imagenRes.Id.ToString()).Result).Returns(imagenRes);
            mockService.Setup(s => s.Delete(imagenRes.Id.ToString()).Result).Returns(true);

            var resCall = await imagenController.Delete(imagenRes.Id.ToString());
            var res = resCall.Result as OkObjectResult;

            mockService.Verify(s => s.Delete(imagenRes.Id.ToString()));
            Assert.Equal(200, res?.StatusCode);

        }
    }
}
